                total = torch.zeros_like(max_per_row)
                K = x.shape[-1]
                for start in range(0, K, chunk_size):
                    end = min(K, start + chunk_size)
                    # è¿™é‡Œ exp_chunk ç›¸å½“äºŽ chunk çš„ä¸´æ—¶å†…å­˜ï¼Œè¾ƒå°
                    exp_chunk = (x[..., start:end] - max_per_row).exp()
