                    #     # attention_mask_4d = _prepare_4d_causal_attention_mask_with_cache_position_custom(None, seq_len_q, seq_len_k, dtype, device, batch_size, sliding_window)
                    #     # if self.layer_idx == 0 or self.layer_idx == 27:
